// App.js
import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Header from "./LeftRight/Header";
import SideBar from "./LeftRight/SideBar";
import EmailList from "./LeftRight/EmailList";
import Compose from "./LeftRight/Compose";
import { useSelector } from "react-redux";
import EmailDetails from "./LeftRight/EmailDetails";
import AuthForm from "./Login/AuthForm";

import { useContext } from "react";
import AuthContext from "./Login/auth-context";
import { useHistory } from "react-router-dom";

function App() {
  const showCompose = useSelector((state) => state.mail.ComposeIsVisible);
  // Use the AuthContext here
  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.token;
  const history = useHistory();

  return (
    <Router>
      <div className="app">
        <Header />
        <div className="app-body">
          <SideBar />
          <Switch>
            <Route path="/" exact>
              <EmailList />
            </Route>
            {!isLoggedIn && <Route path="/auth" element={<AuthForm />}></Route>}

            <Route
              path="/"
              element={isLoggedIn ? history.push("/") : <Navigate to="/auth" />}
            />
            <Route path="/mail">
              <EmailDetails />
            </Route>
          </Switch>
        </div>
        {showCompose && <Compose />}
      </div>
    </Router>
  );
}

export default App;
