import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import { useSelector } from "react-redux";
import AuthForm from "./Login/AuthForm";
import Home from "./Home/Home";
import EmailDetails from "./LeftRight/EmailDetails";
import Compose from "./LeftRight/Compose";

function App() {
  const isLoggedIn = useSelector((state) => state.user.value);

  return (
    <Router>
      <Switch>
        <Route path="/auth">
          {!isLoggedIn ? <AuthForm /> : <Redirect to="/" />}
        </Route>

        {/* Place any other routes here */}
        <Route path="/mail">
          <EmailDetails />
        </Route>

        <Route path="/" exact>
          {isLoggedIn ? <Home /> : <Redirect to="/auth" />}
        </Route>
      </Switch>
      <Compose />
    </Router>
  );
}

export default App;
