import { createSlice } from "@reduxjs/toolkit";

const MailSlice = createSlice({
  name: "mail",
  initialState: {
    ComposeIsVisible: false,
    selectedmessageBody: null,
  },

  reducers: {
    open(state) {
      state.ComposeIsVisible = true;
    },
    close(state) {
      state.ComposeIsVisible = false;
    },
    openBodyMessage(state, action) {
      state.selectedmessageBody = action.payload;
    },
  },
});
export const MailActions = MailSlice.actions;
export default MailSlice;
