import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Header from "./LeftRight/Header";
import SideBar from "./LeftRight/SideBar";
import EmailList from "./LeftRight/EmailList";
import Compose from "./LeftRight/Compose";
import { useSelector } from "react-redux";
import EmailDetails from "./LeftRight/EmailDetails";
import Login from "./Login/Login";
import AuthPage from "./Login/Auth";

function App() {
  const showCompose = useSelector((state) => state.mail.ComposeIsVisible);
  const user = useSelector((state) => state.user.value);
  return (
    <Router>
      {user && (
        <div className="app">
          <Header />
          <div className="app-body">
            <SideBar />
            <Switch>
              <Route path="/" exact>
                <EmailList />
              </Route>
              <Route path="/mail">
                <EmailDetails />
              </Route>
            </Switch>
          </div>
          {showCompose && <Compose />}
        </div>
      )}
      : {<AuthPage></AuthPage>}
    </Router>
  );
}

export default App;
