// App.js
import React from "react";

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import Header from "./LeftRight/Header";
import SideBar from "./LeftRight/SideBar";
import EmailList from "./LeftRight/EmailList";
import Compose from "./LeftRight/Compose";
import { useSelector } from "react-redux";
import EmailDetails from "./LeftRight/EmailDetails";
import AuthForm from "./Login/AuthForm";

import { useContext } from "react";
import AuthContext from "./Login/auth-context";
import { useHistory } from "react-router-dom";

// ... (import statements)

function App() {
  const showCompose = useSelector((state) => state.mail.ComposeIsVisible);
  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.token;
  const history = useHistory();

  // Redirect to '/' when the user is logged in
  // if (isLoggedIn) {
  //   return <Redirect to="/" />;
  // }

  // if (!isLoggedIn) {
  //   return <AuthForm />;
  // }

  return (
    <Router>
      <div className="app">
        <Header />
        <div className="app-body">
          <SideBar />
          <Switch>
            {/* {isLoggedIn && (
              <Route path="/" exact>
                <EmailList />
              </Route>
            )} */}

            {!isLoggedIn && (
              <Route path="/auth">
                <AuthForm />
              </Route>
            )}
            {isLoggedIn && (
              <Route path="/" exact>
                <EmailList />
              </Route>
            )}
            {/* {isLoggedIn && (
              <Route path="/" exact>
                <EmailList />
              </Route>
            )} */}
            <Route path="/mail">
              <EmailDetails />
            </Route>
          </Switch>
        </div>
        {showCompose && <Compose />}
      </div>
    </Router>
  );
}

export default App;
