import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { MailActions } from "./UserSlice"; // Import your Redux actions

import firebase from "firebase/app";
import "firebase/auth";
import "../Login/Login.css";

const Login = () => {
  const dispatch = useDispatch();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const loginHandler = () => {
    // Sign in with email and password
    firebase
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then((userCredential) => {
        // User signed in successfully
        const user = userCredential.user;
        dispatch(MailActions.signIn(user)); // Dispatch the user object to Redux
      })
      .catch((error) => {
        // Handle errors here
        setError(error.message);
      });
  };

  return (
    <div className="loginwrap">
      <div className="login">
        <img
          src="https://i.pinimg.com/736x/82/ee/a2/82eea2c87dc9a870eb5a354eae996b9f.jpg"
          alt="User Profile"
        ></img>
      </div>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button className="gmail-login" onClick={loginHandler}>
        Login With Email/Password
      </button>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default Login;
