import React from "react";
import { Route, Switch, Redirect } from "react-router-dom";
import Header from "./LeftRight/Header";
import SideBar from "./LeftRight/SideBar";
import EmailList from "./LeftRight/EmailList";
import Compose from "./LeftRight/Compose";
import { useSelector } from "react-redux";
function App() {
  const showCompose = useSelector((state) => state.mail.ComposeIsVisible);
  return (
    <div className="app">
      <Header></Header>
      <div className="app-body">
        <SideBar></SideBar>
        <Switch>
          <Route path="/" exact>
            <EmailList></EmailList>
          </Route>

          <Route path="/mail">
            <EmailList></EmailList>
          </Route>
        </Switch>
      </div>
      {showCompose && <Compose></Compose>}
    </div>
  );
}

export default App;
