// App.js
import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Header from "./LeftRight/Header";
import SideBar from "./LeftRight/SideBar";
import EmailList from "./LeftRight/EmailList";
import Compose from "./LeftRight/Compose";
import { useSelector } from "react-redux";
import EmailDetails from "./LeftRight/EmailDetails";

import AuthContext from "./store/auth-context"; // Import your AuthContext
import { useContext } from "react";

function App() {
  const showCompose = useSelector((state) => state.mail.ComposeIsVisible);
  const authCtx = useContext(AuthContext); // Use the AuthContext here
  const isLoggedIn = authCtx.isLoggedIn; // Access isLoggedIn from the context

  return (
    <Router>
      <div className="app">
        <Header />
        <div className="app-body">
          <SideBar />
          <Switch>
            <Route path="/" exact>
              <EmailList />
            </Route>
            {!isLoggedIn && <Route path="/auth" />}

            {isLoggedIn && (
              <Route path="/mail">
                <EmailDetails />
              </Route>
            )}

            <Route path="/compose">
              <Compose />
            </Route>
          </Switch>
        </div>
        {showCompose && <Compose />}
      </div>
    </Router>
  );
}

export default App;
