import "../LeftRight/Compose.css";
import React, { useState } from "react";
import RemoveIcon from "@mui/icons-material/Remove";
import HeightIcon from "@mui/icons-material/Height";
import CloseIcon from "@mui/icons-material/Close";
import ArrowDropDownIcon from "@mui/icons-material/ArrowDropDown";
import FormatColorTextIcon from "@mui/icons-material/FormatColorText";
import AttachFileIcon from "@mui/icons-material/AttachFile";
import LinkIcon from "@mui/icons-material/Link";
import InsertEmoticonIcon from "@mui/icons-material/InsertEmoticon";
import NoteAddIcon from "@mui/icons-material/NoteAdd";
import PhotoCameraBackIcon from "@mui/icons-material/PhotoCameraBack";
import PhonelinkLockIcon from "@mui/icons-material/PhonelinkLock";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import DeleteIcon from "@mui/icons-material/Delete";
import { useDispatch } from "react-redux";
import { MailActions } from "../store/MailSlice";

const Compose = () => {
  const [to, setTo] = useState("");
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");

  const dispatch = useDispatch();
  const ComposeCloseHandler = () => {
    dispatch(MailActions.close());
  };
  const formSubmitHandler = (e) => {
    e.preventDefault();
    if (to === "") {
      return alert("to is required");
    }
    if (subject === "") {
      return alert("subject is required");
    }
    if (message === "") {
      return alert("message is required");
    }
    const mailBody = {
      receiverMail: toMailRef.current.value,
      subject: subjectMailRef.current.value,
      senderName: nameRef.current.value,
      body: convertToRaw(editorState.getCurrentContent()).blocks[0].text,
      isRead: false,
      senderEmail: email,
    };
    fetch(
      "https://mailclientbox-c312c-default-rtdb.firebaseio.com//mail.json",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(mail),
      }
    )
      .then((response) => response.json())
      .then((data) => {
        // Handle success if needed
        console.log("Mail stored in Firebase:", data);
        dispatch(mailAction.addMail(mail));
        toast.success("Mail sent successfully");
        setTimeout(() => {
          props.composeHandler(false);
        }, 1000);
      })
      .catch((error) => {
        // Handle error if needed
        console.error("Error storing mail in Firebase:", error);
        toast.error("Failed to send mail. Please try again.");
        setTimeout(() => {
          props.composeHandler(false);
        }, 1000);
      });
    setEditorState("");
  };

  return (
    <div className="compose">
      <div className="compose-header">
        <div className="compose-header-left">
          <span>New Message</span>
        </div>
        <div className="compose-header-right">
          <RemoveIcon></RemoveIcon>
          <HeightIcon></HeightIcon>
          <CloseIcon onClick={ComposeCloseHandler}></CloseIcon>
        </div>
      </div>
      <form onSubmit={formSubmitHandler}>
        <div className="compose-body">
          <div className="compose-body-form">
            <input
              type="email"
              placeholder="Reciepents"
              value={to}
              onChange={(e) => setTo(e.target.value)} //changeHandler function nahi banaya !! direct kar diya hai.
            ></input>
            <input
              type="text"
              placeholder="Subject"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
            ></input>
            <textarea rows="20" onChange={(e) => setMessage(e.target.value)}>
              {message}
            </textarea>
          </div>
        </div>
        <div className="compose-footer">
          <div className="compose-footer-left">
            <button type="submit">
              Send <ArrowDropDownIcon></ArrowDropDownIcon>
            </button>
          </div>

          <div className="compose-footer-right">
            <FormatColorTextIcon></FormatColorTextIcon>
            <AttachFileIcon></AttachFileIcon>
            <LinkIcon></LinkIcon>
            <InsertEmoticonIcon></InsertEmoticonIcon>
            <NoteAddIcon></NoteAddIcon>
            <PhonelinkLockIcon></PhonelinkLockIcon>
            <PhonelinkLockIcon></PhonelinkLockIcon>
            <PhotoCameraBackIcon></PhotoCameraBackIcon>
            <MoreVertIcon></MoreVertIcon>
            <DeleteIcon></DeleteIcon>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Compose;
