import { createSlice } from "@reduxjs/toolkit";

const UserSlice = createSlice({
  name: "user",
  initialState: {
    value: null,
  },

  reducers: {
    signIn(state, action) {
      state.value = action.payload;
    },
    signOut(state) {
      state.value = null;
    },
  },
});
export const MailActions = UserSlice.actions;
export default UserSlice;
