// App.js
import React from "react";

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";


import Compose from "./LeftRight/Compose";
import { useSelector } from "react-redux";
import EmailDetails from "./LeftRight/EmailDetails";
import AuthForm from "./Login/AuthForm";


import { useContext } from "react";
import AuthContext from "./Login/auth-context";
import { useHistory } from "react-router-dom";
import Home from "./Home/Home";

// ... (import statements)

function App() {
  const showCompose = useSelector((state) => state.mail.ComposeIsVisible);
  const show =useSelector(state=>state.user) 
  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.token;
  const history = useHistory();

  // Redirect to '/' when the user is logged in
  // if (isLoggedIn) {
  //   return <Redirect to="/" />;
  // }

  // if (!isLoggedIn) {
  //   return <AuthForm />;
  // }

  return (
    <Router>
      {/* {!isLoggedIn ? <AuthForm /> : <Home />} */}

      <Switch>
        <Route path="/" exact>
          {!isLoggedIn ? <Redirect to="/auth" /> : <Home />}
        </Route>

        <Route path="/auth">
          <AuthForm />
        </Route>

        <Route path="/mail">
          <EmailDetails />
        </Route>
      </Switch>
      {showCompose && <Compose />}
    </Router>
  );
}

export default App;
