import React, { useState } from "react";
import { Button } from "@material-ui/core";
import CreateIcon from "@mui/icons-material/Create";
import "../LeftRight/SideBar.css";
import SideBarOption from "./SideBarOptions";
import InboxIcon from "@mui/icons-material/Inbox";
import StarRateIcon from "@mui/icons-material/StarRate";
import QueryBuilderIcon from "@mui/icons-material/QueryBuilder";
import LabelImportantIcon from "@mui/icons-material/LabelImportant";
import SendIcon from "@mui/icons-material/Send";
import DraftsIcon from "@mui/icons-material/Drafts";
import DeleteIcon from "@mui/icons-material/Delete";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import VideocamIcon from "@mui/icons-material/Videocam";
import KeyboardIcon from "@mui/icons-material/Keyboard";
import { useDispatch } from "react-redux";
import { MailActions } from "../store/MailSlice";
import EmailDetails from "./EmailDetails";
import EmailBody from "./EmailBody";

const SideBar = ({name}) => {
  const dispatch = useDispatch();
  ///

  const setMailBodyhandler = () => {
    dispatch(
      MailActions.openBodyMessage({
        name,
        subject,
        message,
        email,
        time,
      })
    );
    history.push("/mail");
  };

  const composeOpenHandler = () => {
    dispatch(MailActions.open());
  };
  ///

  return (
    <div className="sidebar">
      <Button
        startIcon={<CreateIcon />}
        className="compose"
        onClick={composeOpenHandler}
      >
        Compose
      </Button>
      <SideBarOption
        Icon={InboxIcon}
        title="Inbox"
        number="123"
        isActive={true}
      />
      <SideBarOption Icon={StarRateIcon} title="Stared" number="155" />
      <SideBarOption Icon={QueryBuilderIcon} title="Snoozed" number="654" />
      <SideBarOption
        Icon={LabelImportantIcon}
        title="Importnant"
        number="165"
      />
      <SideBarOption
        Icon={SendIcon}
        title="Sent"
        number="468"
        onClick={setMailBodyhandler}
      />

      <SideBarOption Icon={DraftsIcon} title="Drafts" number="782" />
      <SideBarOption Icon={DeleteIcon} title="Trash" number="021" />
      <SideBarOption Icon={ExpandMoreIcon} title="More" number="266" />
      <hr></hr>
      <h3 className="sidebaroptions-heading">Meet</h3>
      <SideBarOption Icon={VideocamIcon} title="New Meeting" number="135" />
      <SideBarOption Icon={KeyboardIcon} title="Join A Meeting" number="135" />
    </div>
  );
};

export default SideBar;
