import React from "react";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import Header from "../LeftRight/Header";
import SideBar from "../LeftRight/SideBar";
import EmailList from "../LeftRight/EmailList";
import { Fragment } from "react";
import { UserActions } from "../store/UserSlice"; // Adjust the import path as needed
import './'

const Home = () => {
  const dispatch = useDispatch();
  const history = useHistory();

  const handleLogout = () => {
    // Dispatch the action to log out the user
    dispatch(UserActions.signOut());

    // Optionally, clear any local storage or perform other cleanup
    localStorage.removeItem("token"); // Example: Remove the user token from local storage

    // Redirect the user to the login page
    history.push("/auth");
  };

  return (
    <Fragment>
      <div className="app">
        <Header />
        <div className="app-body">
          <SideBar />
          <EmailList />
        </div>
        <div className="actions">
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
    </Fragment>
  );
};

export default Home;
