import React from "react";
import { Router, Switch, Route } from "react-router-dom";
import Header from "./LeftRight/Header";
import SideBar from "./LeftRight/SideBar";
import EmailList from "./LeftRight/EmailList";
import Compose from "./LeftRight/Compose";
import { useSelector } from "react-redux";
import EmailDetails from "./LeftRight/EmailDetails";
function App() {
  const showCompose = useSelector((state) => state.mail.ComposeIsVisible);
  return (
    <Router>
      <div className="app">
        <Header></Header>

        <div className="app-body">
          <SideBar></SideBar>
          <Switch>
            <Route path="/" exact>
              <EmailList></EmailList>
            </Route>

            <Route path="/mail">
              <EmailDetails></EmailDetails>
            </Route>
          </Switch>
        </div>
        {showCompose && <Compose></Compose>}
      </div>
    </Router>
  );
}

export default App;
